import React, { Component } from 'react'
import PropTypes from 'prop-types'

/**
* @author
* @class Test
**/

class Test extends Component {
 state = {sec:0,hours:0,minutes:0,seconds:0 ,value:0


 }
 valueInput = event=>{
     this.setState({

        sec : parseInt(event.target.value),// convert value to number if it's string
        hours   : Math.floor(sec / 3600), // get hours
        minutes : Math.floor((sec - (hours * 3600)) / 60), // get minutes
        seconds :sec - (hours * 3600) - (minutes * 60), //  get seconds
       // add 0 if value < 10; Example: 2 => 02

     })
 }
 render() {
  return(
   <div className="fedia">
       <center>
           <h1>Timer</h1>
           <hr></hr>
           <input placeholder="Enter the number..." onChange={this.valueInput}></input>

           <hr></hr>
           <h1>{this.state.value}</h1>
       </center>
   </div>
    )
   }
 }


Test.propTypes = {}
export default Test
