import logo from './logo.svg';
import './App.css';

function App() {
  class Test extends React.Component {
    constructor(){
      super()
      this.state = {
        textValue: ''
      }
    } 

    render(){
      return (
        <div>
          <h2>Event is onInput instead of onChange</h2>
          <p>but behaves the same and works better in IE11</p>
          <input
            value={ this.state.textValue }
            onInput={ this.handleInput.bind(this) }
          />
          <p>{ this.state.textValue }</p>
        </div>
      )
    }
    handleInput(e){ 
      this.setState({ textValue: e.target.value })
    }
  }


  ReactDOM.render(
    <Test/>,
    document.getElementById("root")
  );
}

export default App;
