import React, { Component } from 'react'
import PropTypes from 'prop-types'

/**
* @author
* @class Test
**/

class Test extends Component {
 state = { num : 0, 
           hours : 0, 
           minutes : 0, 
           seconds : 0 , 
           value : 0 , 
        };
valueInput = (event) => {
    this.setState({ 
        num : Number(event.target.value)
    })
}
 convertValue = ()=>{
     this.setState({       
        heures : Math.floor(num / 3600),
        minutes : Math.floor(num % 3600 / 60),
        secondes : Math.floor(num % 3600 % 60),
       // add 0 if value < 10; Example: 2 => 02

     })
 }
 render() {
  return(
   <div className="fedia">
       <center>
           <h1>Timer</h1>
           <hr></hr>
           <input placeholder="Enter the number..." onChange={this.valueInput}></input>
            <buttton onClick={this.convertValue}></buttton>
           <hr></hr>
           <h1>{this.state.num}</h1>
       </center>
   </div>
    )
   }
 }



export default Test
